IGNORE = 0
EMPTY = 1
WALL = 2
BOULDER = 3
TARGET = 4


class SokoMap:
    def __init__(self, pos, sokomap):
        self.sokomap = sokomap
        self.pos = pos

    def bfs(self):
        return utils.bfs(*self.pos, walkable=self.sokomap == EMPTY,
                         walkable_diagonally=np.zeros_like(self.sokomap, dtype=bool),
                         can_squeeze=False)

    def move(self, boulder_y, boulder_x, dy, dx):
        assert self.sokomap[boulder_y, boulder_x] == BOULDER
        assert self.sokomap[boulder_y - dy, boulder_x - dx] == EMPTY
        assert self.bfs()[boulder_y - dy, boulder_x - dx] != -1
        assert self.sokomap[boulder_y + dy, boulder_x + dx] in [EMPTY, TARGET]

        self.pos = boulder_y, boulder_x
        self.sokomap[boulder_y, boulder_x] = EMPTY
        if self.sokomap[boulder_y + dy, boulder_x + dx] == EMPTY:
            self.sokomap[boulder_y + dy, boulder_x + dx] = BOULDER
        elif self.sokomap[boulder_y + dy, boulder_x + dx] == TARGET:
            pass
        else:
            assert 0

    def print(self, pos=None):
        mapping = {IGNORE: ' ', EMPTY: '.', WALL: '#', BOULDER: '0', TARGET: '^'}
        for y in range(self.sokomap.shape[0]):
            for x in range(self.sokomap.shape[1]):
                print((mapping[self.sokomap[y, x]] if (y, x) != (pos or self.pos) else '@'), end='')
            print()
        print()


def convert_map(text):
    START = -1
    mapping = {'<': EMPTY, '>': START, '.': EMPTY, '?': EMPTY, '+': EMPTY,
               '0': BOULDER, '-': WALL, '|': WALL, ' ': IGNORE, '^': TARGET}
    ret = []
    for line in text.splitlines():
        if not line:
            continue
        ret.append([mapping[l] for l in line])
    ret = np.array(ret)
    assert len(list(zip(*(ret == TARGET).nonzero()))) == 1
    start = list(zip(*(ret == START).nonzero()))
    assert len(start) == 1
    start = start[0]
    ret[ret == START] = EMPTY
    return SokoMap(start, np.array(ret))


if __name__ == '__main__':
    import termios, tty, sys, os
    termios.tcgetattr(sys.stdin)
    tty.setcbreak(sys.stdin.fileno())
    try:

        # check answers / manually solve
        for text_map, answer in maps.items():
            sokomap = convert_map(text_map)
            if answer is None:
                answer = []

            answer = answer.copy()
            path = []
            while (sokomap.sokomap == BOULDER).sum() > 0 or len(answer) != 0:
                print(path)
                sokomap.print()

                if len(answer) == 0:
                    y, x = sokomap.pos
                    while 1:
                        sokomap.print((y, x))
                        dir = os.read(sys.stdin.fileno(), 3)
                        mapping = {b'j': (1, 0), b'k': (-1, 0), b'h': (0, -1), b'l': (0, 1)}
                        if dir not in mapping:
                            continue
                        dy, dx = mapping[dir]
                        if sokomap.sokomap[y + dy, x + dx] == EMPTY:
                            y, x = y + dy, x + dx
                        elif sokomap.sokomap[y + dy, x + dx] == BOULDER:
                            y, x = y + dy, x + dx
                            break
                else:
                    (y, x), (dy, dx) = answer[0]
                    answer = answer[1:]

                if sokomap.sokomap[y, x] != BOULDER:
                    print('that is not a boulder!')
                    continue
                if sokomap.sokomap[y - dy, x - dx] != EMPTY:
                    print('you cannot stand to push in this direction!')
                    continue
                if sokomap.bfs()[y - dy, x - dx] == -1:
                    print('you cannot get there!')
                    continue
                if sokomap.sokomap[y + dy, x + dx] not in [EMPTY, TARGET]:
                    print('you cannot push in this direction!')
                    continue

                path.append(((y, x), (dy, dx)))

                sokomap.move(y, x, dy, dx)

                print(path)
                sokomap.print()
        print('OK')
    finally:
        os.system('stty sane')
